import { Navigate} from "react-router-dom";
import { getDatabase, ref, set } from "firebase/database";
import firebase from './firebase'
import './loginform.css'



function GoTothanks() {


    const [goTothanks, setGoTothanks] = React.useState(false);
    let formInfo = {}



    if(goTothanks) {
        return <Navigate to="/thanks" />;
    }

    function handleChange(e) {
        const { name, value } = e.target

        formInfo[name] = value;

        // this.setState(
        //     {
        //         [name]: value
        //     }
        // )
    }


    return (<>
            <div className ="topright"><img src='/RSImage.png'></img></div>
            <div className='app_'>
                <div className='login'>
                    <h2 className='title'>Login</h2>
                    <form>
                        <div id='sign-in-button'></div>

                        <input className='answer_' name='name' placeholder='Enter your name' required onChange={handleChange} /><br/>

                    </form>

                </div>
            </div>
            <div className ="bottomright"><img src='/logo.jpg' width="100px" alt=""></img></div>
        </>);


}



export default goTothanks
