import React, { useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom"
import {getQuizAnswers, getAnswerQuiz} from "./globals"

function MakeQuizAnswers() {

    return (
        <>
        <div className='app_'>
    <ol>
        {
            getQuizAnswers().map(function(object, i){
                return (
                <li>{object.questionText}<p>{
                    object.answerOptions.map(function(anAnswer, j){
                        if (anAnswer.isCorrect) {
                            return <p>Correct answer: {anAnswer.answerText}<br/> Your Answer: {getAnswerQuiz()[object.questionText]}</p>;
                        }
                    })
                    }</p>
                    </li>
                )
            })
        }
        </ol>
        <div>						<div><button className="answered here-btn" onClick={()=> {navigate("/Answers")}}>Show Answers</button></div>
</div>
        </div>
        </>
        );

}

export default MakeQuizAnswers;
